import React, { useState, useRef } from "react";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [crop, setCrop] = useState({ unit: "%", width: 30, aspect: 1 / 1 });
  const [croppedImage, setCroppedImage] = useState(null);
  const inputFileRef = useRef(null);
  const imageRef = useRef(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setSelectedImage(URL.createObjectURL(file));
    setCroppedImage(null);
  };

  const handleCropComplete = (crop) => {
    if (imageRef.current && crop.width && crop.height) {
      getCroppedImage(imageRef.current, crop, (croppedImage) => {
        setCroppedImage(croppedImage);
      });
    }
  };

  const getCroppedImage = (image, crop, callback) => {
    const canvas = document.createElement("canvas");
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    const ctx = canvas.getContext("2d");

    canvas.width = crop.width * scaleX;
    canvas.height = crop.height * scaleY;

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width * scaleX,
      crop.height * scaleY
    );

    canvas.toBlob((blob) => {
      const croppedImage = new File([blob], "croppedImage.jpg", {
        type: "image/jpeg",
      });
      callback(URL.createObjectURL(croppedImage));
    }, "image/jpeg");
  };

  const handleImageUpload = () => {
    // Handle the image upload to your Django backend
    // ...
  };

  const handleCropImage = () => {
    if (inputFileRef.current) {
      inputFileRef.current.click();
    }
  };

  return (
    <div className="App">
      <h1>Profile Image Upload</h1>
      <input
        type="file"
        accept="image/*"
        onChange={handleImageChange}
        style={{ display: "none" }}
        ref={inputFileRef}
      />
      {selectedImage && (
        <div>
          <ReactCrop
            src={selectedImage}
            crop={crop}
            onChange={(newCrop) => setCrop(newCrop)}
            onComplete={handleCropComplete}
            onImageLoaded={(image) => (imageRef.current = image)}
          />
          {croppedImage && (
            <div className="image-preview">
              <img src={croppedImage} alt="Cropped" />
            </div>
          )}
        </div>
      )}
      <button onClick={handleCropImage}>Select Image</button>
      <button onClick={handleImageUpload} disabled={!croppedImage}>
        Upload
      </button>
    </div>
  );
}

export default App;
