import {
  Heading,
  Avatar as ChakraAvatar,
  Box,
  Center,
  Image,
  Flex,
  Text,
  Stack,
  Button,
  useColorModeValue,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalCloseButton,
  ModalBody,
  SimpleGrid,
  Card,
  CardBody,
  CardFooter,
  useDisclosure,
  ModalHeader,
} from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import { Avatar as AvatarCrop } from "react-avatar-edit";
export default function Profile() {
  const {
    isOpen: isChooseOpen,
    onOpen: ChooseOpen,
    onClose: ChooseOnClose,
  } = useDisclosure();
  const [src, SetSrc] = useState(null);
  const [preview, setPreview] = useState(null);

  const onCloseAvatar = () => {
    setPreview(null);
  };

  const onCrop = (view) => {
    setPreview(view);
  };

  useEffect(() => {
    console.log(preview);
  }, [preview]);

  const openis = () => {
    ChooseOpen();
  };

  return (
    <div>
      <AvatarCrop
        width={400}
        height={300}
        onCrop={onCrop}
        onClose={onCloseAvatar}
        src={src}
      />

      {preview && <img src={preview} alt="" />}
    </div>
  );
}
