import {
  Heading,
  Avatar as AvatarChakra,
  Box,
  Center,
  Image,
  Flex,
  Text,
  Stack,
  Button,
  useColorModeValue,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
  Input,
  Container,
} from "@chakra-ui/react";
import { useState, useEffect, useCallback } from "react";
import Avatar from "react-avatar-edit";
import axios from "axios";
import Cropper from "react-easy-crop";

import image from "./nio 192.png";

export default function ProfileUpdatePage() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [src, SetSrc] = useState(null);
  const [preview, setPreview] = useState(null);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [img, setImg] = useState("");
  const [showcropper, setShowcropper] = useState(false);

  const onCropComplete = (croppedArea, croppedAreaPixels) => {
    // Handle the cropped area and pixels if needed
    console.log("Cropped Area:", croppedArea);
    console.log("Cropped Area in pixels:", croppedAreaPixels);
  };

  const onCloses = () => {
    setPreview(null);
  };

  const onCrop = (view) => {
    setPreview(view);
  };

  // useEffect(() => {
  //   console.log(preview);
  // }, [preview]);
  const uploadImage = async () => {
    const formData = new FormData();

    formData.append("title", "image");
    formData.append("image", crop);

    try {
      const response = await fetch("http://localhost:8000/image/", {
        method: "POST",
        body: formData,
      })
        .then((res) => console.log(res))
        .catch((error) => console.log(error));
    } catch (error) {
      // Error handling
    }
    setShowcropper(false);

    onClose();
  };

  const [profileImage, setProfileImage] = useState(null);

  // useEffect(() => {
  //   const fetchProfileImage = async () => {
  //     try {
  //       const response = await axios.get("http://localhost:8000/api/image/");
  //       const userProfileImage = response.data.find(
  //         (image) => image.isProfileImage
  //       );
  //       setProfileImage(userProfileImage);
  //     } catch (error) {
  //       // Error handling
  //     }
  //   };

  //   fetchProfileImage();
  // }, []);
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = () => {
      setImg(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };
  return (
    <Center py={6}>
      <Box
        maxW={"270px"}
        w={"full"}
        bg={useColorModeValue("white", "gray.800")}
        boxShadow={"2xl"}
        rounded={"md"}
        overflow={"hidden"}
        mt={15}
      >
        {/* <Image
          h={"120px"}
          w={"full"}
          src={
            "https://images.unsplash.com/photo-1612865547334-09cb8cb455da?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=634&q=80"
          }
          objectFit={"cover"}
        /> */}
        <Flex justify={"center"}>
          <AvatarChakra
            size={"xl"}
            src={preview ? preview : ""}
            alt={"Author"}
            css={{
              border: "2px solid white",
            }}
          />
        </Flex>

        <div>
          {profileImage ? (
            <img src={profileImage.image} alt={profileImage.name} />
          ) : (
            <p></p>
          )}
        </div>

        <Box p={6}>
          <Stack spacing={0} align={"center"} mb={5}>
            <Heading fontSize={"2xl"} fontWeight={500} fontFamily={"body"}>
              John Doe
            </Heading>
            <Text color={"gray.500"}>Frontend Developer</Text>
          </Stack>

          <Stack direction={"row"} justify={"center"} spacing={6}>
            <Stack spacing={0} align={"center"}>
              <Text fontWeight={600}>23k</Text>
              <Text fontSize={"sm"} color={"gray.500"}>
                Followers
              </Text>
            </Stack>
            <Stack spacing={0} align={"center"}>
              <Text fontWeight={600}>23k</Text>
              <Text fontSize={"sm"} color={"gray.500"}>
                Followers
              </Text>
            </Stack>
          </Stack>

          <Button
            onClick={onOpen}
            w={"full"}
            mt={8}
            bg={useColorModeValue("#151f21", "gray.900")}
            color={"white"}
            rounded={"md"}
            _hover={{
              transform: "translateY(-2px)",
              boxShadow: "lg",
            }}
          >
            Update
          </Button>
        </Box>
      </Box>

      <Modal closeOnOverlayClick={false} isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Create your account</ModalHeader>
          <ModalCloseButton />
          <ModalBody pb={6}>
            {img ? (
              <Container>
                <Cropper
                  image={img}
                  crop={crop}
                  zoom={zoom}
                  aspect={4 / 3}
                  onCropChange={setCrop}
                  onCropComplete={onCropComplete}
                  onZoomChange={setZoom}
                />

                <img src={crop} alt="" />
              </Container>
            ) : (
              <Input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
              ></Input>
            )}
          </ModalBody>

          <ModalFooter>
            <Button colorScheme="blue" mr={3} onClick={uploadImage}>
              Save
            </Button>
            <Button onClick={onClose}>Cancel</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
      {/* {img ? (
        <Container>
          <Cropper
            image={img}
            crop={crop}
            zoom={zoom}
            aspect={4 / 3}
            onCropChange={setCrop}
            onCropComplete={onCropComplete}
            onZoomChange={setZoom}
          />
        </Container>
      ) : (
        <Input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
        ></Input>
      )} */}
    </Center>
  );
}
